package com.toaok.study.module.home.activity;import android.content.Context;import android.content.Intent;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import com.toaok.study.R;import com.toaok.study.module.base.presenter.activity.BaseToolbarActivity;import com.toaok.study.module.home.fragemnt.MainFragment;import com.toaok.study.module.home.view.AMainDelegate;/** * 主界面 */public class MainActivity extends BaseToolbarActivity<AMainDelegate> {    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);    }    @Override    protected Class<AMainDelegate> getDelegateClass() {        return AMainDelegate.class;    }    @Override    protected void initToolbarView() {        viewDelegate.setLeftIcon(R.drawable.ic_menu_white);        viewDelegate.setRightIcon(R.drawable.ic_more_vert_white);    }    @Override    protected Fragment createFragment() {        return MainFragment.newInstance("MainFragment");    }    public static void startActivity(Context context) {        Intent intent = new Intent();        intent.setClass(context, MainActivity.class);        context.startActivity(intent);    }    public void setTitle(String title) {        mToolbarBean.setTitleText(getString(R.string.home_map));        notifyModelChanged(mToolbarBean);    }}