package com.toaok.study.module.home.bean;import android.graphics.Bitmap;import android.text.TextUtils;import com.toaok.themvp.model.IModel;/** * @author Toaok * @version 1.0  2018/9/8. */public class SplashBean implements IModel {    private Bitmap image;    private String url;    private String countDownTimer;    private boolean isHaveAd = false;    public SplashBean() {    }    public SplashBean(String url) {        this.url = url;    }    public SplashBean(Bitmap image) {        this.image = image;    }    public SplashBean(Bitmap image, String url) {        this.image = image;        this.url = url;    }    public void setSplashBean(SplashBean splashBean) {        this.url = splashBean.url;        this.image = splashBean.image;        this.countDownTimer = splashBean.countDownTimer;        this.isHaveAd = splashBean.isHaveAd;    }    public Bitmap getImage() {        return image;    }    public void setImage(Bitmap image) {        this.image = image;    }    public String getUrl() {        return url;    }    public void setUrl(String url) {        this.url = url;        if (!TextUtils.isEmpty(url)) {            isHaveAd = true;        }    }    public String getCountDownTimer() {        return countDownTimer;    }    public void setCountDownTimer(String countDownTimer) {        this.countDownTimer = countDownTimer;    }    public boolean isHaveAd() {        return isHaveAd;    }    public void setHaveAd(boolean haveAd) {        isHaveAd = haveAd;    }}