package com.toaok.study.module.other.fragment;import android.Manifest;import android.os.Bundle;import android.view.View;import com.tbruyelle.rxpermissions2.RxPermissions;import com.toaok.study.R;import com.toaok.study.base.presenter.fragment.BaseFragment;import com.toaok.study.module.other.activity.WebActivity;import com.toaok.study.module.other.view.OtherDelegate;import com.toaok.study.utils.AppUtil;public class OtherFragment extends BaseFragment<OtherDelegate> implements View.OnClickListener {    private static final String TAG = "com.toaok.study.module.other.fragment.OtherFragment";    public static OtherFragment newInstance(String desc) {        OtherFragment fragment = new OtherFragment();        Bundle bundle = new Bundle();        bundle.putString(TAG, desc);        fragment.setArguments(bundle);        return fragment;    }    @Override    protected void bindEvenListener() {        super.bindEvenListener();        viewDelegate.setOnClickListener(this, R.id.tv_call_phone, R.id.tv_webview);    }    @SuppressWarnings("unchecked")    @Override    protected Class getDelegateClass() {        return OtherDelegate.class;    }    @Override    public void onClick(View v) {        switch (v.getId()) {            case R.id.tv_call_phone:                new RxPermissions(this).request(Manifest.permission.CALL_PHONE).subscribe(granted -> {                    if (granted) {                        if (getActivity() != null)                            AppUtil.sendMessage(getActivity().getApplicationContext(), "", "dhfds");                    } /*else {                                if (getActivity() != null)                                    AppUtil.openAppDetails(getActivity().getApplicationContext(), "电话");                            }*/                });                break;            case R.id.tv_webview:                WebActivity.startActivity(getActivity(), "https://www.baidu.com/", "百度一下", false,true);                break;        }    }}