package com.toaok.study.utils;import android.Manifest;import android.content.Context;import android.content.Intent;import android.content.pm.PackageManager;import android.net.Uri;import android.support.v4.app.ActivityCompat;/** * @author Toaok * @version 1.0  2018/9/26. */public class AppUtil {    /**     * 调用系统拨打电话     * 需要权限 {@link Manifest.permission#CALL_PHONE }     * @param context    Context     * @param phoneNumber 手机号码     *     */    public static void callPhone(Context context, String phoneNumber) {        Intent intent = new Intent(Intent.ACTION_CALL);        /*          Calling startActivity() from outside of an Activity  context requires the FLAG_ACT        * */        intent.setFlags(Intent.FLAG_ACTIVITY_NEW_TASK );        Uri data = Uri.parse("tel:" + phoneNumber);        intent.setData(data);        if (ActivityCompat.checkSelfPermission(context, Manifest.permission.CALL_PHONE) != PackageManager.PERMISSION_GRANTED) {            return;        }        context.startActivity(intent);    }    /**     * 调用系统发短信界面     * 不需要权限     * @param activity    Activity     * @param phoneNumber 手机号码     * @param smsContent  短信内容     */    public static void sendMessage(Context activity, String phoneNumber, String smsContent) {        /*if (phoneNumber == null || phoneNumber.length() < 4) {            return;        }*/        Uri uri = Uri.parse("smsto:" + phoneNumber);        Intent it = new Intent(Intent.ACTION_SENDTO, uri);        it.putExtra("sms_body", smsContent);        it.setFlags(Intent.FLAG_ACTIVITY_NEW_TASK);        activity.startActivity(it);    }    /**     * 获取系统版本号     *     * @return     */    public static int getAndroidSDKVersion() {        int version = 0;        try {            version = Integer.valueOf(android.os.Build.VERSION.SDK_INT);        } catch (NumberFormatException e) {        }        return version;    }}