package indi.toaok.androiddemo;import android.app.Application;import android.content.Context;import androidx.multidex.MultiDex;import indi.toaok.androiddemo.utils.Utils;/** * @author Toaok * @version 1.0  2018/8/29. */public class App extends Application {    @Override    public void onCreate() {        Utils.init(this);        super.onCreate();    }    @Override    protected void attachBaseContext(Context base) {        super.attachBaseContext(base);        MultiDex.install(this);    }}