package indi.toaok.androiddemo.http.rx;import indi.toaok.androiddemo.http.HostUtil;import indi.toaok.androiddemo.http.retrotfit.RetrofitClient;import java.util.HashMap;/** * @author Toaok * @version 1.0  2018/8/20. */public class RequestManager {    private static HashMap<Class, Object> sRequestManager = new HashMap<>();    @SuppressWarnings("uncheck")    public static <T> T getRequest(Class<T> clazz){        if(!clazz.isInterface())throw new IllegalArgumentException("API declarations must be interfaces.");        T t= (T) sRequestManager.get(clazz);        if(t==null){            t= RetrofitClient.createApi(clazz, HostUtil.getServerHost());            sRequestManager.put(clazz,t);        }        return t;    }}