package indi.toaok.androiddemo.model.vo;import indi.toaok.androiddemo.BuildConfig;import indi.toaok.utils.core.AppUtils;import lombok.Data;/** * APP 基本信息 * * @author Toaok * @version 1.0  2018/9/8. */@Datapublic class AppInfo {    public AppInfo() {        applicationId = BuildConfig.APPLICATION_ID;        buildType = BuildConfig.BUILD_TYPE;        environment = BuildConfig.API_HOST;        channel = BuildConfig.FLAVOR;        versionName = AppUtils.getAppVersionName();        versionCode = AppUtils.getAppVersionCode();    }    /**     * applicationId     */    private String applicationId;    /*     * 开发环境     * */    private String environment;    /**     * 打包方式     */    private String buildType;    /*     *打包渠道     * */    private String channel;    /*     * versionName     * 版本名     * */    private String versionName;    /**     * versionCode     * 版本号     */    private int versionCode;}