package indi.toaok.androiddemo.model.vo;import com.google.gson.Gson;import lombok.Data;/** * @author Toaok * @version 1.0  2018/9/10. */@Datapublic class SplashImageBean {    private int id;    private String url;    public String toJson() {        return new Gson().toJson(this);    }    public static SplashImageBean fromJson(String json) {        return new Gson().fromJson(json, SplashImageBean.class);    }}