package indi.toaok.androiddemo.module.home.databinder;import indi.toaok.androiddemo.base.databinder.BaseDataBinder;import indi.toaok.androiddemo.module.home.bean.SplashBean;import indi.toaok.androiddemo.module.home.view.SplashDelegate;/** * @author Toaok * @version 1.0  2018/9/8. */public class SplashDataBinder<V extends SplashDelegate, D extends SplashBean> extends BaseDataBinder<V, D> {    @Override    public void viewBindModel(V viewDelegate, D data) {        super.viewBindModel(viewDelegate, data);        if (data.isHaveAd()) {            viewDelegate.setImageView(data.getUrl());            viewDelegate.setCountDownTimer(data.getCountDownTimer());        }    }}