package indi.toaok.androiddemo.module.home.view;import android.graphics.Bitmap;import android.graphics.drawable.Drawable;import androidx.annotation.DrawableRes;import android.text.TextUtils;import android.util.Log;import android.view.View;import android.widget.FrameLayout;import android.widget.ImageView;import android.widget.TextView;import butterknife.BindView;import indi.toaok.androiddemo.R;import indi.toaok.androiddemo.base.view.BaseDelegate;import indi.toaok.imageloder.core.ImageLoder;/** * View视图层，完全移除与Presenter业务逻辑的耦合 * * @author Toaok * @version 1.0  2018/9/8. */public class SplashDelegate extends BaseDelegate {    @BindView(R.id.iv_splash)    ImageView mImageView;    @BindView(R.id.frame_layout)    FrameLayout mFrameLayout;    @BindView(R.id.tv_count_down_timer)    TextView mCountDownTimer;    @Override    public int getRootLayoutId() {        return R.layout.activity_splash;    }    public TextView getCountDownTimer() {        return mCountDownTimer;    }    public void setImageView(@DrawableRes int resId) {        if (mImageView != null) {            mImageView.setImageResource(resId);        }    }    public void setImageView(Drawable drawable) {        if (mImageView != null) {            mImageView.setImageDrawable(drawable);        }    }    public void setImageView(Bitmap bitmap) {        if (mImageView != null) {            mImageView.setImageBitmap(bitmap);        }    }    public void setImageView(String url) {        if (mImageView != null && !TextUtils.isEmpty(url)) {            Log.d("ImageView", url);            ImageLoder.loadImage(mImageView, url);        }    }    public void setCountDownTimer(CharSequence countDownTimer) {        if (!TextUtils.isEmpty(countDownTimer)) {            mCountDownTimer.setVisibility(View.VISIBLE);            mCountDownTimer.setText(countDownTimer);        }    }    public FrameLayout getFrameLayout() {        return mFrameLayout;    }}