package indi.toaok.androiddemo.module.other.view;import android.annotation.SuppressLint;import androidx.recyclerview.widget.LinearLayoutManager;import androidx.recyclerview.widget.RecyclerView;import butterknife.BindView;import indi.toaok.androiddemo.R;import indi.toaok.androiddemo.base.view.BaseDelegate;/** * @author Toaok * @version 1.0  2018/9/8. */public class OtherDelegate extends BaseDelegate {    @BindView(R.id.recycler_view_other)    RecyclerView mRecyclerView;    @Override    public int getRootLayoutId() {        return R.layout.fragment_other;    }    @SuppressLint("WrongConstant")    @Override    public void initView() {        super.initView();        mRecyclerView.setLayoutManager(new LinearLayoutManager(getActivity(), LinearLayoutManager.VERTICAL, false));        mRecyclerView.setHasFixedSize(true);    }    public void setAdapter(RecyclerView.Adapter adapter) {        if (adapter != null) {            mRecyclerView.setAdapter(adapter);        }    }}