package indi.toaok.base.presenter.fragment;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.view.View;import indi.toaok.base.view.BaseDelegate;import indi.toaok.themvp.databind.DataBindFragment;import indi.toaok.themvp.databind.DataBinder;/** * 基准BaseFragment 所有在该项目中使用TheMVP框架的BaseFragment都必须继承该抽象类 * * @author Toaok * @version 1.0  2018/9/27. */public abstract class BaseFragment<T extends BaseDelegate> extends DataBindFragment<T> implements View.OnClickListener {    /**     * 如果该Fragment需要进行数据绑定，必须重新该方法     *     * @return 返回数据绑定类     */    @Override    protected DataBinder getDataBinder() {        return null;    }    /*    * 注意：在Fragment中做一些与View有关的操作，都应该在该方法执行    * 不然会因为控件没有初始化，造成空指针异常    * */    @Override    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {        super.onViewCreated(view, savedInstanceState);    }    @Override    public void onDestroy() {        super.onDestroy();       /*       * huwei emui 8.0 memory leaks       * */       if(binder!=null){           binder=null;       }       if(getArguments()!=null){           getArguments().clear();       }    }    /**     * 上次点击时间     */    private long lastClick = 0;    /**     * 判断是否快速点击     *     * @return {@code true}: 是<br>{@code false}: 否     */    private boolean isFastClick() {        long now = System.currentTimeMillis();        if (now - lastClick >= 300) {            lastClick = now;            return false;        }        return true;    }    @Override    public void onClick(View view) {        if (isFastClick()) return;    }}