package indi.toaok.base.view;import android.support.annotation.StringRes;import indi.toaok.themvp.view.AppDelegate;/** * 基准BaseDelegate 所有在该项目中使用TheMVP框架的BaseDelegate都必须继承该抽象类 * * @author Toaok * @version 1.0  2018/9/26. */public abstract class BaseDelegate extends AppDelegate {    protected String getString(@StringRes int resId){        if(getActivity()==null){            return "";        }        return getActivity().getString(resId);    }    protected String getString(@StringRes int resId,Object ...formatArgs){        if(getActivity()==null){            return "";        }        return getActivity().getString(resId,formatArgs);    }}