package indi.toaok.model.local.db.dao.impl;import android.content.Context;import android.database.sqlite.SQLiteDatabase;import indi.toaok.model.local.db.DataBaseHelper;import indi.toaok.model.local.db.dao.ExtendDao;/** * @author Toaok * @version 1.0  2018/9/11. */public abstract class BaseDaoImpl<T> implements ExtendDao<T> {    protected DataBaseHelper mHelper;    protected SQLiteDatabase db;    protected BaseDaoImpl(Context context) {        mHelper = DataBaseHelper.getInstance(context);    }    @Override    public void close() {        if (db != null) {            db.close();        }    }    protected  SQLiteDatabase getReadableDatabase(){        return mHelper.getReadableDatabase();    }    protected  SQLiteDatabase getWritableDatabase(){        return mHelper.getWritableDatabase();    }}