package indi.toaok.module.base.view;import android.graphics.drawable.Drawable;import android.support.annotation.DrawableRes;import android.support.annotation.Nullable;import android.support.v7.widget.Toolbar;import android.view.View;import android.widget.ImageButton;import android.widget.TextView;import indi.toaok.R;import indi.toaok.base.view.BaseDelegate;/** * 带有toolbar的View * * @author Toaok * @version 1.0  2018/9/26. */public class BaseToolbarDelegate extends BaseDelegate {    private TextView titleTextView;    private TextView rightTextView;    private ImageButton rightImagButton;    private OnClickToolBarListener mToolBarListener;    @Override    public int getRootLayoutId() {        return R.layout.activity_fragment;    }    Toolbar toolbar;    @Override    public Toolbar getToolbar() {        toolbar = rootView.findViewById(R.id.toolbar);        return toolbar;    }    @Override    public void initWidget() {        super.initWidget();        titleTextView = get(R.id.text_title);        rightTextView = get(R.id.text_right);        rightImagButton = get(R.id.btn_right);        initToolbarEvent();    }    public void setToolBarListener(OnClickToolBarListener toolBarListener) {        mToolBarListener = toolBarListener;    }    public interface OnClickToolBarListener {        void onClickLeftButton();        void onClickRightButton();        void onClickRightText();    }    /**     * 初始化toolbar事件     */    private void initToolbarEvent() {        toolbar.setNavigationOnClickListener(v -> mToolBarListener.onClickLeftButton());        rightTextView.setOnClickListener(v -> mToolBarListener.onClickRightText());        rightImagButton.setOnClickListener(v -> mToolBarListener.onClickRightButton());    }    public void setLeftIcon(@DrawableRes int resId) {        toolbar.setNavigationIcon(resId);    }    public void setLeftIcon(@Nullable Drawable icon) {        toolbar.setNavigationIcon(icon);    }    public void setTitle(CharSequence title) {        titleTextView.setVisibility(View.VISIBLE);        titleTextView.setText(title);    }    public void setRightText(CharSequence rightText) {        rightTextView.setVisibility(View.VISIBLE);        rightTextView.setText(rightText);    }    public void setRightIcon(@DrawableRes int resId) {        rightImagButton.setVisibility(View.VISIBLE);        rightImagButton.setImageResource(resId);    }    public void setRightIcon(@Nullable Drawable icon) {        rightImagButton.setVisibility(View.VISIBLE);        rightImagButton.setImageDrawable(icon);    }}