package indi.toaok.module.home.activity;import android.content.Context;import android.content.Intent;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import android.view.MenuItem;import indi.toaok.Config;import indi.toaok.R;import indi.toaok.module.base.presenter.activity.BaseToolbarActivity;import indi.toaok.module.home.fragemnt.MainFragment;import indi.toaok.module.home.view.AMainDelegate;import indi.toaok.module.other.activity.WebActivity;/** * 主界面 */public class MainActivity extends BaseToolbarActivity<AMainDelegate> implements AMainDelegate.OnMenuItemSelectListener {    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        viewDelegate.setMenuItemSelectListener(this);    }    @Override    protected Class<AMainDelegate> getDelegateClass() {        return AMainDelegate.class;    }    @Override    protected void initToolbarView() {        viewDelegate.setLeftIcon(R.drawable.ic_menu_white);        viewDelegate.setRightIcon(R.drawable.ic_more_vert_white);    }    @Override    protected Fragment createFragment() {        return MainFragment.newInstance("MainFragment");    }    public static void startActivity(Context context) {        Intent intent = new Intent();        intent.setClass(context, MainActivity.class);        context.startActivity(intent);    }    public void setTitle(String title) {        mToolbarBean.setTitleText(title);        notifyModelChanged(mToolbarBean);    }    /**     * Called when an item in the navigation menu is selected.     *     * @param item The selected item     * @return true to display the item as the selected item     */    @Override    public boolean onMenuItemSelect(@NonNull MenuItem item) {        switch (item.getItemId()) {            case R.id.action_git_hub:                WebActivity.startActivity(this, Config.GITHUB, getString(R.string.menu_item_git_hub), true, true);                return true;            case R.id.action_blog:                WebActivity.startActivity(this, Config.BLOG, getString(R.string.menu_item_blog), true, true);                return true;            case R.id.action_website:                WebActivity.startActivity(this, Config.BLOG, getString(R.string.menu_item_website), true, true);                return true;            case R.id.action_about:               WebActivity.startActivity(this, Config.BLOG, getString(R.string.menu_item_about), true, true);               return true;            default:                break;        }        return false;    }}