package indi.toaok.utils;import android.content.Context;import android.graphics.Typeface;import java.util.HashMap;import java.util.Map;/** * @author Toaok * @version 1.0  2018/9/28. */public class TypefaceUtil {    private static Map<String, Typeface> sTypefaceMap;    /**     * 获取Typeface     * @param context     * @param filepath {@link font/MaterialIcons-Regular.ttf}     * @return     */    public static Typeface getTypeface(Context context, String filepath) {        Typeface typeface;        if (sTypefaceMap == null) {            sTypefaceMap = new HashMap<>();        }        typeface = sTypefaceMap.get(filepath);        if (typeface == null) {            typeface = Typeface.createFromAsset(context.getAssets(), filepath);            sTypefaceMap.put(filepath, typeface);        }        return typeface;    }}